<template>
  <div class="version">
    <div class="version-content" v-if="versionData.length">
      <div class="version-item">
        <p>包名：{{versionData[0].appPackage}}</p>
        <p>版本号：{{versionData[0].appVersion}}</p>
        <p>内部版本号：{{versionData[0].appVersionCode}}</p>
        <p>下载地址：<a target="_blank" :href="versionData[0].httpUrl">{{versionData[0].httpUrl}}</a></p>
        <img
          src="data:image/png;base64,"
          alt="">
        <p>
          <el-button @click="androidFlag=true">上传app</el-button>
        </p>
      </div>
      <div class="version-item">
        <p>包名：{{versionData[1].appPackage}}</p>
        <p>版本号：{{versionData[1].appVersion}}</p>
        <p>内部版本号：{{versionData[1].appVersionCode}}</p>
        <p>下载地址：<a target="_blank" :href="versionData[1].httpUrl">{{versionData[1].httpUrl}}</a></p>
        <img
          src="data:image/png;base64,"
          alt="">
        <p>
          <el-button @click="androidFlag=true">上传app</el-button>
        </p>
      </div>
    </div>
    <el-dialog width="300px" title="上传app" :visible.sync="androidFlag">
        <el-input v-model="httpUrl" ></el-input>
        <el-upload
          style="margin-top:20px;"
          :action="action"
          class="upload-demo"
          ref="upload"
          :headers="uploadHead"
          :on-success="successFun"
          :on-error="errorFun"
          :before-upload="beforeAvatarUpload"
          :file-list="fileList"
          :auto-upload="false">
          <el-button slot="trigger" size="small" type="primary">选取文件</el-button>
          <el-button style="margin-left: 10px;" size="small" type="success" @click="submitUpload">上传到服务器</el-button>
        </el-upload>
      <div slot="footer" class="dialog-footer">
        <el-button @click="androidFlag = false">取 消</el-button>
      </div>
    </el-dialog>

  </div>
</template>

<script>
  import { getToken } from '@/utils/auth'
  export default {
    name: 'version',
    data() {
      return {
        versionData: [],
        androidFlag: false,
        iosFlag: false,
        httpUrl: '',
        action: window.urlData.url + '/app?httpUrl=',
        fileList: [],
        uploadHead: {
          Authorization: getToken()
        },
      }
    },
    created() {
      this.$store.dispatch('getVersionDetails').then((res) => {
        this.versionData = res;
      }).catch()
    },
    methods: {
      submitUpload() {
        this.$refs.upload.submit();
      },
      successFun(file) {
        this.$message.success('上传成功')
        console.log(file)
      },
      errorFun(err) {
        this.$message.error(err)
      },

      beforeAvatarUpload() {
        this.action = this.action + this.httpUrl;
      }
    }
  }
</script>

<style rel="stylesheet/scss" lang="scss" scoped>
  .version {
    padding: 20px;
    & .version-content {
      display: flex;
      justify-content: space-between;
      width: 800px;
      margin: 0 auto;
      & .version-item p {
        margin-bottom: 10px;
      }
    }
  }
</style>
